let initialState={
    cart:[],
    totalItems:0,
    
};
const addToCart=(state=initialState,action)=>
{
    switch(action.type)
    {
        case "ADDTOCART":
            return state[0][action.payload];

        default: return state;
    }
}

export default addToCart;
