export const updateCartItem =(book)=>{
    return {
        type:'UPDATE_CART_ITEM',
        payload:book
    }
}

export const updateCartItemQuantity = (book, quantity) => ({
    type: UPDATE_CART_ITEM_QUANTITY,
    book,
    quantity
})

export const clearCart = () => ({
    type: CLEAR_CART,
})
