import {  updateCartItem } from '../Action/Action';

const addToCart=(state = { items: [] },action)=>
{
    switch(action.type)
    {
        case 'UPDATE_CART_ITEM':
            const item = action.payload;
            state.push({ ...item });
              
        default:
            return state;
    }
}

export default addToCart;
