import { CLEAR_CART, UPDATE_CART_ITEM,UPDATE_CART_ITEM_QUANTITY } from './cart.actions';
let initialState={
    cart:[],
    totalItems:0,
    qty:0
};
const addToCart=(state=initialState,action)=>
{
    switch(action.type)
    {
        case "ADDTOCART":
            return {
                ...state
            }

        default: return state;
    }
}

export default addToCart;
