import React from 'react';
import { useSelector } from 'react-redux';

function Cart() {
    const books = useSelector((books) => books.addToCart);
    return (
        <>
            <div className='container mt-5'>
                <div className='row'>
                <h4>There Are {books.cart.length} books in your cart.</h4>
                <table className="table text-center">
                    <thead>
                        <tr>
                            <th>Firstname</th>
                            <th>Lastname</th>
                            <th>Email</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            books.cart.map((book) =>

                                <tr>
                                    <td><img src={book.image} alt={book.book_name} style={{ 'width': '100px', 'height': '100px' }} /></td>
                                    <td>{book.book_name}</td>
                                    <td>{book.price}</td>
                                    <td>{book.author}</td>
                                </tr>
                            )
                        }

                    </tbody>
                </table>
                </div>
            </div>

        </>
    )
}

export default Cart;
