import { CLEAR_CART, UPDATE_CART_ITEM,UPDATE_CART_ITEM_QUANTITY } from '../Action/Action';

const addToCart=(state = { items: [] },action)=>
{
    switch(action.type)
    {
        case "ADDTOCART":
            return {
                ...state
            }

        default: return state;
    }
}

export default addToCart;
