const initialState={ cart: [ ]}
const addToCart=(state =initialState ,action)=>
{
    switch(action.type)
    {
        case 'UPDATE_CART_ITEM':
            const item = action.payload;
            state.push({ ...item });
            return state;
        
              
        default:
            return state;
    }
}

export default addToCart;
